body.script:
document.addEventListener('DOMContentLoaded', function() {
    // County data for demo purposes
    const countyData = {
        'CA': ['Los Angeles', 'San Diego', 'Orange', 'Riverside', 'San Bernardino'],
        'TX': ['Harris', 'Dallas', 'Tarrant', 'Bexar', 'Travis'],
        'FL': ['Miami-Dade', 'Broward', 'Palm Beach', 'Hillsborough', 'Orange'],
        'NY': ['Kings', 'Queens', 'New York', 'Suffolk', 'Bronx'],
        'IL': ['Cook', 'DuPage', 'Lake', 'Will', 'Kane']
    };

    // State dropdown change handler
    const stateSelect = document.getElementById('state');
    const countySelect = document.getElementById('county');

    stateSelect.addEventListener('change', function() {
        const selectedState = this.value;
        countySelect.innerHTML = 'Select County';

        if (selectedState) {
            countyData[selectedState].forEach(county => {
                const option = document.createElement('option');
                option.value = county.toLowerCase().replace(' ', '-');
                option.textContent = county;
                countySelect.appendChild(option);
            });
        }
    });

    // Search button handler
    const searchBtn = document.getElementById('searchBtn');
    const resultsContainer = document.getElementById('results');
    const spinner = document.getElementById('spinner');

    searchBtn.addEventListener('click', function() {
        // Validate inputs
        const state = stateSelect.value;
        const county = countySelect.value;
        const address = document.getElementById('address').value;
        const zip = document.getElementById('zip').value;

        if (!state || !county || !address || !zip) {
            alert('Please fill in all fields');
            return;
        }

        // Show loading spinner
        spinner.style.display = 'block';
        resultsContainer.style.display = 'none';

        // Simulate API call with timeout
        setTimeout(function() {
            // Hide spinner
            spinner.style.display = 'none';

            // Show results (demo data)
            document.getElementById('precinct-number').textContent = Math.floor(Math.random() * 1000) + 1;
            document.getElementById('polling-location').textContent = 'Local Community Center';
            document.getElementById('polling-address').textContent = '123 Main St, ' + countySelect.options[countySelect.selectedIndex].text + ', ' + stateSelect.value + ' ' + zip;
            document.getElementById('polling-hours').textContent = '7:00 AM - 8:00 PM';
            document.getElementById('election-day').textContent = 'November 8, 2023';

            resultsContainer.style.display = 'block';
        }, 1500);
    });

    // Download APK button handler
    const downloadBtn = document.getElementById('downloadApk');
    downloadBtn.addEventListener('click', function(e) {
        e.preventDefault();
        alert('APK download would start here. This is a demo.');
        // In a real app, you would link to the actual APK file
        // window.location.href = 'path/to/app.apk';
    });
});

